import "./mymatches.css"
import React, { useState } from "react";
import { SendHorizontal } from 'lucide-react';
import MessageCard from "../../components/messages/messagecard.jsx";
import MessageList from '../../components/messages/messagelist.jsx';

const MyMatches = () => {

    const MessageInput = ({ value, onChange, onKeyPress, onSend }) => {
        return (
          <div className="message-input">
            <textarea
              value={value}
              onChange={onChange}
              onKeyPress={onKeyPress}
              placeholder="Type a message..."
              rows="1"
              className="message-text-input"
            />
            <button className="messageSendButton" onClick={onSend}>
              <i className="icon"><SendHorizontal /></i>
            </button>
          </div>
        );
    };

    // const UserChatInfo = ({ user }) => {
    //     return (
    //         <div className="chatHeading">
    //             <div className="chatImage">
    //                 <img src={selectedUser.matchimage} alt={selectedUser.name} />
    //             </div>
    //             <div className="chatHeader">
    //                 <h1>{selectedUser.name}</h1>
    //             </div>
    //             <div className="chatSubHeader">
    //                 <h2>{selectedUser.classification}</h2>
    //             </div>
    //         </div>
    //     );
    // }

    const UserChatInfo = ({ user }) => {
        return (
            <div className="chatHeading">
                <div className="chatImageBox">
                    <img className="chatImage" src={user.matchimage} alt={user.name} />
                </div>
                <div className="chatHeader">
                    <h1>{user.name}</h1>
                </div>
                <div className="chatSubHeader">
                    <h2>{user.classification}</h2>
                </div>
            </div>
        );
    }

    const [selectedUser, setSelectedUser] = useState(null);
    const handleMatchClick = (user) => {
        setSelectedUser(user);
        // Here you might also want to fetch the chat messages for this user
        console.log('Selected User:', user);
    };

    return (
        <article className="mMArticle">
            <div className="messageBoxContainer">

                <div className="sideBarContainer">
                    <div className="messageHeader">
                        <h1>Messages</h1>
                    </div>
                    <div className="messageList">
                        <MessageList onMatchClick={handleMatchClick}/>
                    </div>
                </div>

                <div className="chatPageContainer">

                    {selectedUser ? (
                        <>   
                            <UserChatInfo user={selectedUser} />

                            <div className="chatBoxContainer">
                                <h1>Insert Chat Here</h1>
                            </div>

                            <div className="chatInputContainer">
                                <MessageInput />        
                            </div>  
                        </>
                    ) : (
                        <>
                        <div className="chatHeader">
                                <h1>No User Selected</h1>
                        </div>
                        <div className="chatBoxContainer">
                                <h1>Select a chat to start messaging.</h1>
                        </div>
                        <div className="chatInputContainer">
                                <MessageInput />        
                        </div>  
                        </>
                    )}
                </div>

            </div>
        </article>
    )
}

export default MyMatches;
