import React, {useState} from "react";
import "./signin.css";
import 'reactjs-popup/dist/index.css';
import  {useNavigate} from 'react-router-dom';
import axios from 'axios'

const baseURL = 'http://127.0.0.1:12000' //change port number after proxying


const SignInPopup = ({ onClose }) => {
  const [inputs, setInputs] = useState({
    email: '',
    password: ''
  })

  navigate = useNavigate()

  const handleChange = (e) => {
    const {name, value} = e.target;
    setInputs((prevData) => ({
      ...prevData,
      [name]: value
    }));
  }

  async function handleSubmit(){
    try{
      const response = await axios.get(`${baseURL}/login`, )

      if (response.ok) {
        navigate('/dashboard')
      }
    } catch (error){
      console.log(error)
    }

  }

    return (
      <div className="popup-overlay">
        <div className="popup-content">
          <button onClick={onClose} className="SignUpClose">Close</button>
          <div className="signupcontent">
            <h2 className="question">Welcome Back!!</h2>
            <h2 lassName="SignUp">Sign In</h2>
              <Form onSubmit={handleSubmit}>
                <input 
                  name="email" 
                  onChange={handleChange} 
                  type="text" 
                  placeholder="Email" required 
                />

                <input 
                  type="password" 
                  placeholder="Password" required 
                />

              </Form>
            </div>

              <Link to="/dashboard" onClick={onClose} className="SignUpLogin">Login</Link>
        </div>
      </div>
    );
  };

  export default SignInPopup;
