import "./messagecard.css";
import React, { useEffect, useState } from "react";
import MessageCard from "./messagecard";

const MessageList = () => {
    const [acceptedMatches, setAcceptedMatches] = useState([]);
    useEffect(() => {
        const storedMatches = localStorage.getItem('acceptedMatches');

        if (storedMatches) {
            // Parse the JSON string back into an array of objects
            setAcceptedMatches(JSON.parse(storedMatches));
        }
        // If nothing is in local storage yet, acceptedMatches will remain an empty array
    }, []);

    return (
        <div className="message-card-container">
            {acceptedMatches.length > 0 ? (
            acceptedMatches.map((match, index) => (
                <MessageCard
                    id={match.id}
                    name={match.name}
                    classification={match.classification}
                    matchimage={match.matchimage}
                    courses={match.courses}
                />
            ))) : (
                <p>No messages yet</p>
            )}


        </div>
    );
}

export default MessageList;
