import React, {useState} from "react";
import "./signup.css";
import 'reactjs-popup/dist/index.css';
import  {useNavigate} from 'react-router-dom';
import axios from 'axios';


const baseURL = 'http://127.0.0.1:10000/api' //change port number after proxying

const SignUpPopup = ({ onClose}) => {
  const [inputs, setInputs] = useState({
    username: '',
    first_ame: '',
    lastName: '',
    email: '',
    password: ''
  })
  const navigate = useNavigate()

  const handleChange = (e) => {
    const { name, value } = e.target;
    setInputs((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  }

  const handleSubmit = (e) =>{
    e.preventDefault();
    try{
      const response = axios.post(`${baseURL}/register`, inputs)

      if (response.ok) {
        navigate('/accountsettings')
      } else {
        console.log('Sign Up Failed!')
      }
    }
    catch (error){
      console.log('Error submitting sign up form',error)
    }
  }

    return (
      <div className="popup-overlay">
        <div className="popup-content">
          <button onClick={onClose} className="SignUpClose">Close</button>
          <div className="signupcontent">
            <h2 className="question">Want to find Study Buddies?</h2>
            <h2 className="SignUp">Sign Up</h2>
              <form onSubmit={handleSubmit}>
                <input 
                  value={inputs.username} 
                  onChange={handleChange} 
                  name="username" 
                  id="username"
                  type="text" 
                  placeholder="Username" required 
                />

                <input 
                  value={inputs.firstName}
                  onChange={handleChange} 
                  name="firstName" 
                  id="firstName" 
                  type="text" 
                  placeholder="First Name" required 
                />

                <input 
                  value={inputs.lastName}
                  onChange={handleChange} 
                  name="lastName" 
                  id="lastName" 
                  type="text" 
                  placeholder="Last Name" required 
                />

                <input 
                  value={inputs.email}
                  onChange={handleChange} 
                  name="email" 
                  id="email" 
                  type="text" 
                  placeholder="Email" required 
                />

                <input 
                  value={inputs.password}
                  onChange={handleChange} 
                  name="password" 
                  id="password"
                  type="password" 
                  placeholder="Password" required 
                  />

                <button type="submit" onSubmit={handleSubmit} className="SignUpLogin">Sign Up</button>

              </form>
            </div>
        </div>
      </div>
    );
  };


export default SignUpPopup;
