import React, { useState, useEffect } from "react";
import "./accountsettings.css"
import defaultprofilepic from "../../assets/defaultPfP.png";
import axios from 'axios';

const AccountSettings = () => {
  const [formData, setFormData] = useState({
    first_name: "", 
    last_name: "",
    phone_number: "",
    email: "",
    classification: "", 
    courses: "", // might need to handle this differently if it's an array on the backend
    profilePic: null, // For file upload
    bio: "",
    username: "" // might add this later
  });

  const [profileImageURL, setProfileImageURL] = useState(null);
  const token = localStorage.getItem('authToken'); //where I'm storing the token that the user was assigned at sign up
  const baseURL = 'http://localhost:10000/api';

  useEffect(() => {
    const fetchUserSettings = async () => { //grabbing the users previous accoutn settings
      if (!token) { //if the user does not have a token/user id
        console.log("Token not found");
        return;
      }
      else {
        console.log("Token found:", token);
      }

      try {
        const response = await axios.get(`${baseURL}/accountsettings`, { //
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        console.log("Posted to database:", response.data);

        setFormData((prevData) => ({
          ...prevData,
          first_name: response.data.first_name || "",
          last_name: response.data.last_name || "",
          phone_number: response.data.phone_number || "",
          email: response.data.email || "",
          classification: response.data.classification || "",
          courses: response.data.courses || "", // Adjust if backend sends courses as an array
          bio: response.data.description || "", // Assuming 'description' in backend is bio
          profileImageURL: response.data.profile_image || null, // Set existing profile image URL
          username: response.data.username || ""
        }));
        setProfileImageURL(response.data.profile_image || null);

      } catch (error) {
        console.error("Error fetching account settings:", error);
      }
    };

    fetchUserSettings();
  }, [token, baseURL]);

  // Handle Input Changes
  const handleChange = (e) => {
    const { name, value, type, files } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === "file" ? files[0] : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!token) {
      console.log("Token not found");
      return;
    }

    const formDataToSend = new FormData();
    for (const key in formData) {
      formDataToSend.append(key, formData[key]);
    }
    console.log("Saved Data:", formData);

    try {
      const response = await axios.put(`${baseURL}/accountsettings`, formDataToSend, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'multipart/form-data',
        },
      });
      console.log("Account settings updated successfully:", response.data);
    } catch (error) {
      console.error("Error updating account settings:", error);
    }
  };

  const [image, setImage] = useState(null);  // state to hold the image

  // Load the saved profile picture from localStorage when the component mounts
  useEffect(() => {
    const savedImage = localStorage.getItem('profilePic');
    if (savedImage) {
      setImage(savedImage); // If there's a saved image, set it to state
    }
  }, []);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData((prevData) => ({
        ...prevData,
        profilePic: file
      }));
      const imageUrl = URL.createObjectURL(file); // Create a temporary URL for the image
      setProfileImageURL(imageUrl); // Set the image URL in state
      // localStorage.setItem('profilePic', imageUrl); // Store the image URL in localStorage
    }
  };


  return (
    <article className="settingsContainer">
      <h1 id="astitle">Account Settings</h1>

      <form onSubmit={handleSubmit} className="settingsInfo">
        <div className="mainInfo">
          <div className="entryarea">
            <span>First Name</span>
            <input type="text" name="firstName" value={formData.first_name} onChange={handleChange} required />
          </div>

          <div className="entryarea">
            <span>Last Name</span>
            <input type="text" name="lastName" value={formData.last_name} onChange={handleChange} required />
          </div>

          <div className="entryarea">
            <span>Phone Number</span>
            <input type="text" name="phonenum" value={formData.phone_number} onChange={handleChange} />
          </div>

          <div className="entryarea">
            <span>Email</span>
            <input type="text" name="email" value={formData.email} onChange={handleChange} required />
          </div>

          <div className="entryarea">
            <span>Courses</span>
            <input type="text" name="courses" value={formData.courses} onChange={handleChange} required />
          </div>

          <div className="entryarea">
            <span>Classification</span>
            <input type="text" name="classification" value={formData.classification} onChange={handleChange} required />
          </div>

        </div>

        <div className="otherInfo">
          <div className="entryarea">
            <span className="aspfpSpan">Edit Profile Picture</span>

              <input type="file"
              name="profilePic"
              accept="image/*"
              onChange={handleImageChange}
              className="asProfileUpload" 
              id="profilePicUpload"
              />

              <label htmlFor="profilePicUpload">
                  <div className="asProfilePic">
                    <img src={profileImageURL || defaultprofilepic} alt="Profile" className="asprofile-image" placeholder={defaultprofilepic}/>
                  </div>
              </label>
          </div>

          <div className="entryarea">
            <span>Bio</span>
            <input type="text" name="bio" placeholder="Tell us about you! What are your interests, activities, favorites?" value={formData.bio} onChange={handleChange} required />
          </div>

          <div className="entryarea">
            <span>Username</span>
            <input type="text" name="instagram" value={formData.username} onChange={handleChange} required />
          </div>

        </div>

        <div id="sEButton">
          <button type="submit" id="saveEdits">
            Save Edits
          </button>
        </div>
      </form>
    </article>
  );
};

/* Add in autocomplete for courses and classification */

export default AccountSettings;
