import React, {useState, Form, SubmitButton} from "react";
import Popup from 'reactjs-popup';
import "./signup.css";
import 'reactjs-popup/dist/index.css';
import  {Link, useNavigate} from 'react-router-dom';
import axios from 'axios';


const baseURL = 'http://127.0.0.1:12000' //change port number after proxying

const SignUpPopup = ({ onClose}) => {
  const [inputs, setInputs] = useState({
    username: '',
    firstName: '',
    lastName: '',
    email: '',
    password: ''
  })
  const navigate = useNavigate()

  async function handleSubmit(){
    try{
      const response = await axios.post(`${baseURL}/register`, inputs)

      if (response.ok) {
        navigate('/accountsettings')
      } else {
        console.log('Sign Up Failed!')
      }
    }
    catch (error){
      console.log('Error submitting sign up form',error)
    }
  }

    return (
      <div className="popup-overlay">
        <div className="popup-content">
          <button onClick={onClose} className="SignUpClose">Close</button>
          <div className="signupcontent">
            <h2 className="question">Want to find Study Buddies?</h2>
            <h2 className="SignUp">Sign Up</h2>
              <Form onSubmit={handleSubmit}>
                <input 
                  value={inputs.username} 
                  onChange={(e) => setInputs({...inputs, username: e.target.value})} 
                  name="username" 
                  id="username"
                  type="text" 
                  placeholder="Username" required 
                />

                <input 
                  name="firstName" 
                  id="firstName" 
                  type="text" 
                  placeholder="First Name" required 
                />

                <input type="text" placeholder="Last Name" required />

                <input type="text" placeholder="Email" required />

                <input type="password" placeholder="Password" required />

                <SubmitButton onClick={onClose} className="SignUpLogin">Sign Up</SubmitButton>
              </Form>
            </div>
        </div>
      </div>
    );
  };


export default SignUpPopup;
