import React, { useState } from "react";
import "./requests.css";

const Requests = ({ requests, handleAccept, handleDecline }) => {
    return (
        <article className="reqsArticle">
            <div className="dbMatchRequestsBox">
                <div className="dbMatchRequests">
                    {requests.length > 0 ? (
                        requests.map((request) => (
                            <div key={request.id} className="dbMatchRequestItem">
                                <p>{request.name} - {request.year}</p>
                                <div className="dbRequestButtons">
                                    <button className="acceptButton" onClick={() => handleAccept(request.id)}>
                                        Accept
                                    </button>
                                    <button className="declineButton" onClick={() => handleDecline(request.id)}>
                                        Decline
                                    </button>
                                </div>
                            </div>
                        ))
                    ) : (
                        <div className="dbMatchRequestsExample">
                            No Requests
                        </div>
                    )}
                </div>
            </div>
        </article>
    );
};

export default Requests;
