import React, { useState } from 'react';
import './matchcard.css';
import PropTypes from 'prop-types';
import { motion, useMotionValue, useTransform, useAnimation } from 'framer-motion';
// Needed to install npm install prop-types to use this
// Need to install npm install framer-motion

const MatchCard = ({ id, name, classification, matchimage, bio, courses, interests, index, totalcards, removeCard }) => {
    const motionValue = useMotionValue(0);
    const rotateValue = useTransform(motionValue, [-200, 200], [-50, 50]);
    const opacityValue = useTransform(motionValue, [-200, -150, 0, 150, 200], [0, 1, 1, 1, 0]);
    const animControls = useAnimation();

    const [isHidden, setIsHidden] = useState(false);

    return (
        <motion.div 
            className={`mc-card ${isHidden ? 'mc-hidden' : ''}`}
            drag="x"
            style={{
                x: motionValue,
                rotate: rotateValue,
                opacity: opacityValue,
                zIndex: totalcards - index,
            }}
            dragConstraints={{ left: -1000, right: 1000 }}
            animate={animControls}
            onDragEnd={(event, info) => {
                if (Math.abs(info.point.x) > 150) {
                    removeCard(id);
                    setIsHidden(true);
                } else {
                    animControls.start({ x: 0 });
                }
            }}
        >
            <div className="mc-image-box">
                <img className="mc-image" src={matchimage} alt="Current Match" />
                <h1 className="mc-name">{name}</h1>
                <p className="mc-classification">{classification}</p>
            </div>

            <div className="mc-info">
                <div className="mc-section">
                    <div className="mc-title">About Me</div>
                    <div className="mc-section-content">{bio}</div>
                </div>

                <div className="mc-section">
                    <div className="mc-title">Courses</div>
                    <div className="mc-section-content">
                        {courses.map((course, index) => (
                            <div className="mc-list-item" key={index}>{course}</div>
                        ))}
                    </div>
                </div>

                <div className="mc-section">
                    <div className="mc-title">Interests</div>
                    <div className="mc-section-content">
                        {interests.map((interest, index) => (
                            <div className="mc-list-item" key={index}>{interest}</div>
                        ))}
                    </div>
                </div>
            </div>
        </motion.div>
    );
}

MatchCard.propTypes = {
    id: PropTypes.string.isRequired,
    name: PropTypes.string.isRequired,
    classification: PropTypes.string.isRequired,
    matchimage: PropTypes.string.isRequired,
    bio: PropTypes.string.isRequired,
    courses: PropTypes.arrayOf(PropTypes.string).isRequired,
    interests: PropTypes.arrayOf(PropTypes.string).isRequired,
    index: PropTypes.number.isRequired,
    totalcards: PropTypes.number.isRequired,
    removeCard: PropTypes.func.isRequired,
};

export default MatchCard;
